mutation productCreateMedia($productId: ID!, $media: [CreateMediaInput!]!) {
  productCreateMedia(productId: $productId, media: $media) {
    media {
      alt
      mediaContentType
      status
      preview {
        image {
          url
        }
      }
    }
    mediaUserErrors {
      field
      message
    }
    product {
      id
      title
    }
  }
}

query getProductMediaStatus($id: ID!) {
  product(id: $id) {
    id
    media(first: 25) {
      edges {
        node {
          ... on MediaImage {
            id
            status
            alt   
            image {
              id
              url
            }
          }
        }
      }
    }
  }
}

parameters.jsCode:
// This is the final, robust version. It gets all data from the Merge node that runs right before it.

// --- 1. GATHER ALL DATA ---
// The data is now perfectly combined in the input.
const readyProductData = $input.item.json.data.product; 
const packagedData = $input.item.json.packagedData;

const printifyProduct = packagedData.printifyProduct;
const shopifyProduct = packagedData.shopifyProduct;
const allCuratedImageUrls = packagedData.allCuratedImageUrls;
const htmlDescription = packagedData.htmlDescription;

// --- 2. BUILD THE MAPS (No changes here) ---
const fingerprintMediaMap = new Map();
readyProductData.media.edges.forEach(edge => {
  const mediaNode = edge.node;
  if (mediaNode.alt && mediaNode.id) {
    fingerprintMediaMap.set(mediaNode.alt, mediaNode.id);
  }
});

const shopifyVariantMap = new Map();
shopifyProduct.variants.edges.forEach(edge => {
  if (edge.node) {
    shopifyVariantMap.set(edge.node.title, edge.node.id);
  }
});

// --- 3. ASSEMBLE THE FINAL PAYLOAD (No changes here) ---
const variantsUpdatePayload = [];
const assignedVariants = new Set(); 

for (const printifyVariant of printifyProduct.variants) {
    const shopifyVariantId = shopifyVariantMap.get(printifyVariant.title);
    const primaryImageUrl = allCuratedImageUrls.find(url => url.includes(`/${printifyVariant.id}/`));

    if (shopifyVariantId && primaryImageUrl && !assignedVariants.has(shopifyVariantId)) {
        const urlParts = primaryImageUrl.split('/');
        const printifyProductId = urlParts[4];
        const printifyMockupId = urlParts[6];
        const fingerprint = `ppid-${printifyProductId}-pvid-${printifyVariant.id}-pmid-${printifyMockupId}`;
        const shopifyMediaId = fingerprintMediaMap.get(fingerprint);

        if (shopifyMediaId) {
            variantsUpdatePayload.push({
                id: shopifyVariantId,
                mediaId: shopifyMediaId
            });
            assignedVariants.add(shopifyVariantId);
        }
    }
}

// --- 4. RETURN THE FINAL COMMAND ---
// ... at the end of the node

// --- 4. RETURN THE FINAL COMMAND ---
return [
  {
    json: {
      variantpayload:
        {
      productId: shopifyProduct.id,
      variants: variantsUpdatePayload
        },
      // Also pass the full data package along this branch
      packagedData: packagedData
    }
  },
  {
    json: {
      htmlDescription: htmlDescription,
      // Also pass the full data package along this branch
      packagedData: packagedData
    }
  }
];

// This node gathers all the data we need for the final assignment
// and packages it up so it doesn't get lost in the loop.

const mergedData = $input.item.json;
const printifyProduct = $('Create_Printify_Product').item.json;
const shopifyProduct = $('Search Shopify Product (GraphQL)').item.json.data.products.edges[0]?.node;
const allCuratedImageUrls = JSON.parse($('Preserve_Product_Data').item.json.curated_image_url);

// --- ADD THIS LINE ---
// Get the perfect HTML description from the payload preparation step.
const htmlDescription = $('Prepare_Printify_Product_Payload').item.json.printifyPayload.description;

const packagedData = {
  printifyProduct: printifyProduct,
  shopifyProduct: shopifyProduct,
  allCuratedImageUrls: allCuratedImageUrls,
  htmlDescription: htmlDescription // Add the description to our package
};

return {
  json: {
    ...mergedData,
    packagedData: packagedData // Our new "baton"
  }
};

// In "Split_Product_Types" (ID: d1bb84ef-a798-4c7b-8c15-64726de190ca)
const items = $input.all(); // Items from "Merge Customer With Transformations"
const outputItems = [];

// Customer data (item without asset_id, usually from Extract Parameters via the merge)
const customerDataItem = items.find(item => item.json && item.json.email && !item.json.asset_id && !item.json.public_id);
// Image items (items with asset_id/public_id from Transformation node via the merge)
const imageItems = items.filter(item => item.json && (item.json.asset_id || item.json.public_id));

if (!customerDataItem) {
  console.error("Split_Product_Types: Customer data not found. Ensure 'Merge Customer With Transformations' is correctly merging.");
  return [{ json: { error: "Customer data item not found." } }];
}
const customerData = customerDataItem.json;

if (imageItems.length === 0) {
  console.warn("Split_Product_Types: No image items found to process.");
}

imageItems.forEach(item => {
  const imageJson = item.json; // This is the rich data from the Transformation node

  // promptIndex and theme_name should now be directly available in imageJson
  const promptIndex = imageJson.promptIndex;
  const actualThemeName = imageJson.theme_name;

  if (promptIndex === undefined || !actualThemeName) {
    console.error(`Split_Product_Types: Missing promptIndex or theme_name for image item. Public ID: ${imageJson.public_id}. Skipping.`);
    return; // Skip this item
  }

  // Use petname from customerData (more reliable) or fallback to imageJson if it somehow has it
  const petName = customerData.petname || imageJson.petname || "Pet";
  const petKind = customerData.petkind || imageJson.petkind || "Pet";
  const phoneNumber = customerData.phoneNumber || imageJson.phoneNumber || "Pet";
  const petGender = $input.first().json.petGender|| imageJson.petgender || "Pet";
  const sessionId = customerData.session_id || imageJson.session_id || Date.now().toString().slice(-8);

  const baseHandle = `${petName.toLowerCase().replace(/\s+/g, '-')}-${actualThemeName.toLowerCase().replace(/[^a-z0-9-]+/g, '').replace(/\s+/g, '-')}`;
  const sessionSuffix = sessionId.slice(-8);
  const uniqueHandle = `${baseHandle}-${sessionSuffix}`;

  // Use spread operator to preserve original field order, then override specific fields
  outputItems.push({
    json: {
      // Start with customer data
      session_id: sessionId,
      email: customerData.email || imageJson.email,
      phoneNumber: phoneNumber,
      petname: petName,
      petGender: petGender,
      petKind: customerData.petKind || imageJson.petKind,
      customer_name: customerData.customer_name || imageJson.customer_name,

      // Include all cloudinary data (preserves original order)
      ...imageJson,

      // Override/ensure these specific fields are set correctly
      promptIndex: promptIndex,
      collection_name: `${petName} ${actualThemeName}`,
      collection_handle: uniqueHandle,
      theme_name: actualThemeName,
      collection_image_url: imageJson.original_url // Use original URL for collection image
    }
  });
});

return outputItems;

// This node's purpose is to control what data the loop's "Done" output collects.
// It looks back and fetches the result from the 'Create_Printify_Product' node
// from this specific iteration.
const productData = $('Create_Printify_Product').first();

// By returning this specific data, we ensure it's what gets aggregated
// after all the loop iterations are complete.
return productData;

