return outputItems;

// In "Prepare_Printify_Product_Payload" (ID: d98090aa-3355-4fc8-bc41-63373c3e29c7)

// --- NEW: Read all variant data from the Google Sheet node ---
const sheetData = $('Get_Variant_Pricing_From_Sheet').all();

// --- NEW: Create Maps for fast lookups. One for prices, one for descriptions ---
const variantPriceMap = new Map();
const blueprintDescriptionMap = new Map();

for (const item of sheetData) {
  const { variant_id, price, blueprint_id, product_description } = item.json;

  // Map each variant_id to its price
  if (variant_id && price !== undefined) {
    variantPriceMap.set(String(variant_id), parseInt(price, 10));
  }

  // Map each blueprint_id to a description. This avoids storing redundant descriptions.
  // We only store the first valid description we find for a blueprint.
  if (blueprint_id && product_description && !blueprintDescriptionMap.has(String(blueprint_id))) {
    blueprintDescriptionMap.set(String(blueprint_id), product_description);
  }
}
console.log(`Loaded ${variantPriceMap.size} prices and ${blueprintDescriptionMap.size} descriptions from the sheet.`);


// 1. Get the original data from the start of the loop
const productInfo = $('Loop Product Templates2').item.json;

// 2. Get the result of the image upload
const uploadedImage = $('Upload_Image_To_Printify').item.json;

// 3. Get the result of the variants API call
const providerVariantsResponse = $input.item.json;

// --- Error Checking ---
if (!productInfo || !productInfo.product_title) {
  throw new Error("Could not find original product data from the loop.");
}
if (!uploadedImage || !uploadedImage.id) {
  throw new Error("Could not find a valid image ID from the upload step.");
}
if (!providerVariantsResponse || !providerVariantsResponse.variants) {
  throw new Error("Could not find a valid variants array from the API call.");
}

const placeholderPosition = providerVariantsResponse.variants?.[0]?.print_areas?.[0]?.placeholders?.[0]?.position || "front";
const requiredVariantIds = productInfo.required_variant_ids;

// --- UPDATED LOGIC: Set price for each variant based on the Google Sheet ---
const variants = providerVariantsResponse.variants
  .filter(variant => requiredVariantIds.includes(variant.id))
  .map(variant => {
    // Look up the price from our map
    const priceFromSheet = variantPriceMap.get(String(variant.id));

    if (priceFromSheet === undefined) {
      console.warn(`⚠️ Price for variant ID ${variant.id} not found in Google Sheet. Using fallback price.`);
    }

    return {
      id: variant.id,
      price: priceFromSheet || 2999, // Use price from sheet or fallback to 2999
      is_enabled: true
    };
  });

if (variants.length === 0) {
    throw new Error(`Could not find any matching variants for the provided IDs: ${requiredVariantIds.join(', ')}`);
}

const all_variant_ids = variants.map(v => v.id);

// --- MODIFIED DESCRIPTION HANDLING: Now uses the description exactly as it is in the Google Sheet ---
let description = blueprintDescriptionMap.get(String(productInfo.blueprint_id));
if (!description) {
  console.warn(`⚠️ Description for blueprint ID ${productInfo.blueprint_id} not found. Using generic fallback.`);
  // Ensure the fallback also uses HTML if you expect it for consistency,
  // or adjust as per your desired fallback format.
  description = `<p>Custom ${productInfo.theme_name} artwork featuring ${productInfo.petname}.</p>`;
}

// Only replace placeholders like {{petname}} and {{theme_name}}.
// No other HTML stripping or line break conversions are performed,
// preserving the exact content from your Google Sheet.
const finalDescription = description
  .replace(/{{petname}}/g, productInfo.petname)
  .replace(/{{theme_name}}/g, productInfo.theme_name);


// --- REVISED PLACEMENT SETTINGS ---
const imagePlacementMap = {
  // T-shirt
  12: { x: 0.5, y: 0.5045, scale: 0.8, angle: 0 },
  // Color Accent Mug
  635: { x: 0.20573430849374247, y: 0.4950372661929266, scale: 0.31111111111111106, angle: 0 },
  // Framed Poster
  764: { x: 0.5, y: 0.5, scale: 1, angle: 0 },
  // Canvas Portrait
  937: { x: 0.5, y: 0.5000067762626349, scale: 0.9999999999999997, angle: 0 }
};

const phoneCasePlacements = {
  'iphone-case': { x: 0.5, y: 0.5, scale: 1, angle: 0 },
  'samsung-case': { x: 0.5445914804458678, y: 0.5082242999343495, scale: 1, angle: 0 },
  'pixel-case': { x: 0.5068214281791004, y: 0.49128903472278723, scale: 1.0136428563582005, angle: 0 }
};

const fallbackPlacement = { x: 0.5, y: 0.5, scale: 1, angle: 0 };
let placement;

// Check if the product is a phone case (blueprint_id 421)
if (productInfo.blueprint_id === 421) {
  // Use the mockup_style to get the correct placement
  placement = phoneCasePlacements[productInfo.mockup_style] || fallbackPlacement;
} else {
  // Use the blueprint_id for all other products
  placement = imagePlacementMap[productInfo.blueprint_id] || fallbackPlacement;
}

// --- Payload Assembly ---
const payload = {
  "title": productInfo.product_title,
  "description": finalDescription, // Use the new clean description
  "blueprint_id": productInfo.blueprint_id,
  "print_provider_id": productInfo.print_provider_id,
  "variants": variants,
  "print_areas": [
    {
      "variant_ids": all_variant_ids,
      "placeholders": [
        {
          "position": placeholderPosition,
          "images": [
            {
              "id": uploadedImage.id,
              "x": placement.x,
              "y": placement.y,
              "scale": placement.scale,
              "angle": placement.angle
            }
          ]
        }
      ]
    }
  ],
  "tags": [
    $('When Executed by Another Workflow').first().json.email,
    $('When Executed by Another Workflow').first().json.petGender,
    $('When Executed by Another Workflow').first().json.petKind,
    $('When Executed by Another Workflow').first().json.session_id,
    $('When Executed by Another Workflow').first().json.petname,
    productInfo.theme_name
  ]
};

// --- THIS IS THE FINAL, CORRECTED LOGIC ---
// Add print_details object for the canvas product, as you discovered.
if (productInfo.blueprint_id === 937) {
  payload.print_details = {
    "print_on_side": "mirror" // Use "regular" for print on sides
  };
  console.log(`✅ Added print_details object for Canvas Portrait.`);
}
// Return the final data
return {
  json: {
    ...productInfo,
    mockup_style: productInfo.mockup_style,
    printifyPayload: payload
  }
};

