// Get all input items
const items = $input.all();

// Find the product/variant payload
const productItem = items.find(i => i.json.variantpayload);
const descriptionItem = items.find(i => i.json.htmlDescription);

// Merge into one payload
const variables = {
  input: {
    id: productItem.json.variantpayload.productId,
    variants: productItem.json.variantpayload.variants || [],
    bodyHtml: descriptionItem.json.htmlDescription
  }
};

// GraphQL payload
const payload = {
  query: `
    mutation productUpdate($input: ProductInput!) {
      productUpdate(input: $input) {
        product { id }
        userErrors { field message }
      }
    }
  `,
  variables
};

// Return a single JSON object for the next node
return {
  json: payload
};

