// ------------------------------------------------------------------------
//  READ CURRENT PRODUCT + PAYLOAD + REQUIRED VARIANTS
// ------------------------------------------------------------------------

const createdProduct = $input.item.json;
const productInfo = $('Prepare_Printify_Product_Payload').item.json;
const blueprintId = productInfo.blueprint_id;
const mockupStyle = productInfo.mockup_style;
const requiredVariantIds = productInfo.required_variant_ids;

const newProductId = createdProduct.id;
const curatedImageUrls = [];

// ------------------------------------------------------------------------
//  NEW SECTION: CHECK AVAILABILITY OF REQUIRED VARIANTS
// ------------------------------------------------------------------------

const requiredVariants = createdProduct.variants.filter(v =>
  requiredVariantIds.includes(v.id)
);

const unavailable = requiredVariants.filter(v => v.is_available === false);

if (unavailable.length > 0) {
  // STOP workflow before publish
  return [{
    json: {
      shouldPublish: false,
      unavailableVariants: unavailable,
      message: "Some required variants are unavailable",
      ...createdProduct
    }
  }];
}


// ------------------------------------------------------------------------
//  EXISTING LOGIC (unchanged) TO CONSTRUCT MOCKUPS
// ------------------------------------------------------------------------

if (mockupStyle && variantSpecificMockups[mockupStyle]) {
  const config = variantSpecificMockups[mockupStyle];
  const templateName = config.template_name;
  const enabledVariants = createdProduct.variants.filter(v => requiredVariantIds.includes(v.id));

  for (const variant of enabledVariants) {
    const mockupsForThisVariant = config.variants[variant.id];
    if (mockupsForThisVariant) {
      for (const mockup of mockupsForThisVariant) {
        const imageUrl = `https://images.printify.com/mockup/${newProductId}/${variant.id}/${mockup.id}/${templateName}.jpg?camera_label=${mockup.camera_label}`;
        curatedImageUrls.push(imageUrl);
      }
    }
  }
} 
else if (simpleMockupMap[blueprintId]) {
  const config = simpleMockupMap[blueprintId];
  if (config.generatePerVariant) {
    const enabledVariants = createdProduct.variants.filter(v => requiredVariantIds.includes(v.id));
    for (const variant of enabledVariants) {
      for (const mockup of config.mockups) {
        const imageUrl = `https://images.printify.com/mockup/${newProductId}/${variant.id}/${mockup.id}/${config.template_name}.jpg?camera_label=${mockup.camera_label}`;
        curatedImageUrls.push(imageUrl);
      }
    }
  } else {
    const defaultVariant = createdProduct.variants.find(v => v.is_default);
    if (!defaultVariant) { throw new Error(`Could not find a default variant for product ${newProductId}`); }
    const referenceVariantId = defaultVariant.id;
    for (const mockup of config.mockups) {
      const imageUrl = `https://images.printify.com/mockup/${newProductId}/${referenceVariantId}/${mockup.id}/${config.template_name}.jpg?camera_label=${mockup.camera_label}`;
      curatedImageUrls.push(imageUrl);
    }
  }
} 
else {
  console.warn(`⚠️ No mockup configuration found for blueprint_id: ${blueprintId} or style: ${mockupStyle}.`);
}


// ------------------------------------------------------------------------
//  RETURN FINAL OUTPUT + shouldPublish FLAG
// ------------------------------------------------------------------------

return [{
  json: {
    shouldPublish: true,
    curatedImageUrls,
    ...createdProduct
  }
}];
