const body = $json.body || {};
let editRequest = "modify"; // Default
const imageData = [];

// Adjust based on actual Paperform keys
if (body.data && Array.isArray(body.data)) {
  body.data.forEach(field => {
    // Thereâ€™s no 'petDescription' in the sample input, so default stays
    // Update key if that field is later added to the form

    if (field.key === '8objr' && Array.isArray(field.value)) {
      // 8objr = Pet Photos
      field.value.forEach((photo, index) => {
        if (photo.url) {
          imageData.push({
            url: photo.url,
            filename: photo.name || `image${index}.jpeg`,
            mimeType: photo.type || 'image/jpeg',
            index: index
          });
        }
      });
    }
  });
}

// Return individual items for each image
return imageData.map(img => ({
  json: {
    imageUrl: img.url,
    imageName: img.filename,
    imageType: img.mimeType,
    imageIndex: img.index,
    totalImages: imageData.length,
    requestType: "Modify my Product Image",
    editRequest: editRequest,
    submittedAt: new Date().toISOString(),
    session_id: body.submission_id || "unknown"
  }
}));

const item = $input.item; // Current item being processed

// Create a copy of binary data to modify
const binaryData = {};
if (item.binary) {
  // Fix MIME types for all binary properties
  for (const key in item.binary) {
    binaryData[key] = {...item.binary[key]};

    // Ensure all jpg images use the correct mime type
    if (binaryData[key].mimeType === 'image/jpg') {
      binaryData[key].mimeType = 'image/jpeg';
    }
  }
}

// Create a new output item with both the prompt and binary data
return {
  json: {
    promptIndex: item.json.promptIndex,
    prompt: item.json.prompt,
    theme_name: item.json.theme_name,
    n: 1,
    quality: "high",//medium,high, auto
    size: "1024x1536",
    output_format: "jpeg",
    email:$json.email
  },
  binary: binaryData // Use the corrected binary data
};

// In the Code3 node, update to include submissionId
const items = $input.all();
const binaryOutput = {};
const imageFiles = [];
const submissionId = items[0]?.json?.submissionId || 'default';

// Process all items with downloads
items.forEach((item, index) => {
  if (item.binary && item.binary.data) {
    // Copy binary data with the correct key name
    binaryOutput[`image${index}`] = item.binary.data;

    // Build image metadata
    imageFiles.push({
      index: index,
      filename: item.json.imageName || `image${index}.jpg`,
      mimeType: item.json.imageType || 'image/jpeg'
    });
  }
});

// Get metadata from the first item
const firstItem = items[0] || {};
const metadata = {
  requestType: firstItem.json?.requestType || "Modify my Product Image",
  editRequest: firstItem.json?.editRequest || "modify",
  imageCount: imageFiles.length,
  imageFiles: imageFiles,
  submittedAt: firstItem.json?.submittedAt || new Date().toISOString(),
  submissionId: submissionId
};

// Return a single item with all image data
return [{
  json: metadata,
  binary: binaryOutput
}];

// In "Transformation" (ID: 3206a9f0-c977-4669-aae0-33983e3e4c23)
// Input item is the output of "Combine Cloudinary with Details"
const combinedItemJson = $input.item.json;

const publicId = combinedItemJson.public_id; // From Cloudinary part of combinedItemJson
const cloudName = "myfurryprints";
const maskId = "Mask3larger_jpm63l"; // Ensure this mask ID is correct

const imageTransformations = {
  clothing: `https://res.cloudinary.com/${cloudName}/image/upload/e_upscale/f_webp/c_scale,w_4000/l_${maskId},w_4000,fl_cutter/${publicId}`,
  other: `https://res.cloudinary.com/${cloudName}/image/upload/e_upscale/f_webp/c_scale,w_4000/${publicId}`,
  watermark:
`https://res.cloudinary.com/${cloudName}/image/upload/l_MFP-Watermark_k1usnu.png/${publicId}`   
};

// Pass through all fields from combinedItemJson and add/overwrite transformation URLs
return {
  json: {
    ...combinedItemJson, // This includes Cloudinary data AND original details like theme_name, promptIndex, email etc.
    original_url: combinedItemJson.secure_url, // Using secure_url from Cloudinary for consistency
    collection_image_url: imageTransformations.watermark,
    transformations: imageTransformations,
    clothing_url: imageTransformations.clothing,
    other_url: imageTransformations.other
  }
};

// Node Name: Combine Cloudinary with Details
// Mode: Run Once For All Items

// All Cloudinary responses from the UploadTOCloudery node
const cloudinaryResponses = $input.all();

// All original items that were the input to UploadTOCloudery
// These are the outputs of the 'Prepare Cloudinary Upload' node from its last full run.
const allOriginalDetailsItems = $('Prepare Cloudinary Upload1').all();

if (cloudinaryResponses.length === 0) {
  console.warn("Combine Cloudinary: No Cloudinary responses received.");
  return [];
}
if (allOriginalDetailsItems.length === 0) {
  console.error("Combine Cloudinary: No original details found from 'Prepare Cloudinary Upload'. Cannot merge.");
  // Return items indicating error or an empty array
  return cloudinaryResponses.map(cr => ({ json: { ...cr.json, error: "Missing original details for merging." } }));
}

const outputItems = [];

cloudinaryResponses.forEach((cloudinaryItem, loopIndex) => {
  const cloudinaryResponseJson = cloudinaryItem.json; // This is the actual Cloudinary API response data

  let matchingOriginalDetails = null;

  // Strategy 1: Match using public_id (from Cloudinary) and customFilename (from original details)
  // Cloudinary public_id is often "folder/customFilename" or just "customFilename"
  const publicIdParts = cloudinaryResponseJson.public_id.split('/');
  const cloudinaryFilenamePart = publicIdParts[publicIdParts.length - 1]; // e.g., "session_id_promptX"

  const foundItem = allOriginalDetailsItems.find(
    origItem => origItem.json.customFilename === cloudinaryFilenamePart
  );
  if (foundItem) {
    matchingOriginalDetails = foundItem.json;
  }

  // Strategy 2: Fallback to index-based matching if filename match failed (less ideal but a backup)
  // This assumes UploadTOCloudery processes and outputs items in the same order it received them.
  if (!matchingOriginalDetails && cloudinaryResponses.length === allOriginalDetailsItems.length) {
    console.warn(`Combine Cloudinary: Could not match Cloudinary response (public_id: ${cloudinaryResponseJson.public_id}) to original details by filename. Falling back to index ${loopIndex}.`);
    if (allOriginalDetailsItems[loopIndex]) {
        matchingOriginalDetails = allOriginalDetailsItems[loopIndex].json;
    }
  }

  if (!matchingOriginalDetails) {
    console.error(`Combine Cloudinary: CRITICAL - Could not find matching original details for Cloudinary response with public_id: ${cloudinaryResponseJson.public_id}. Skipping this item.`);
    outputItems.push({ json: { ...cloudinaryResponseJson, error: "Orphaned Cloudinary response - no matching original details." } });
    return; // Skips to the next iteration of forEach
  }

  // Successfully matched, now combine them
  const combinedJson = {
    // All fields from Cloudinary's response
    ...cloudinaryResponseJson, // This includes asset_id, public_id, secure_url, existing, etc.

    // All fields from the matching originalDetails
    // This will overwrite any same-named fields from cloudinaryResponseJson if they exist in matchingOriginalDetails,
    // but mostly they should be distinct (e.g., theme_name, promptIndex are from originalDetails)
    ...matchingOriginalDetails
  };

  outputItems.push({ json: combinedJson });
});

if (outputItems.length !== cloudinaryResponses.length) {
    console.warn(`Combine Cloudinary: Processed ${outputItems.length} items but received ${cloudinaryResponses.length} Cloudinary responses. Some items may have been skipped due to matching errors.`);
}

return outputItems;

if (!$json.result) {
  return [];
}

const jobData = JSON.parse($json.result);

// Extract images from customer_data array
const customerData = jobData.customer_data || [];
const imageField = customerData.find(item => item.key === '8objr');
const images = imageField ? imageField.value : [];


// ðŸ‘‰ Get phone number by key (from your sample it's "455ij")
