// 1. Grab all merged items
const allItems = $input.all();

// 2. Pull out the single customer info object
const userDataItem = allItems.find(item => !!item.json.email);
if (!userDataItem) {
  throw new Error("Could not find customer data item (email) in input");
}
const customerData = userDataItem.json;

// 3. Pull out all the items that have binary data (i.e. real images)
const imageItems = allItems.filter(item => item.binary && Object.keys(item.binary).length);
if (imageItems.length === 0) {
  throw new Error("No generated images found in input");
}

// 4. Pull original prompt metadata from the Keep Metadata node
//    (Use the node name exactly as it appears in your canvas â€” here I'm using "Keep Metadata")
const promptMetadataItems = $('keep metadata').all();
if (!promptMetadataItems.length) {
  throw new Error("Could not find prompt metadata items");
}

// 5. Build the upload-ready items
return imageItems.map(imageItem => {
  const pi = imageItem.json.promptIndex;
  const promptMeta = promptMetadataItems.find(m => m.json.promptIndex === pi);
  if (!promptMeta) {
    throw new Error(`No prompt metadata found for promptIndex ${pi}`);
  }

  const session_id    = customerData.session_id;
  const customFilename = `${session_id}_prompt${pi}`;

  return {
    json: {
      session_id:    session_id,
      email:         customerData.email,
      petname:       customerData.petname,
      petKind:       customerData.petKind,
      customer_name: customerData.customer_name,
      promptIndex:   pi,
      theme_name:    promptMeta.json.theme_name,
      customFilename,
    },
    binary: {
      // pass through the same binary field you got from Convert to File
      data: imageItem.binary.data  
    }
  };
});

return $input.all().map(item => ({
  json: {
    promptIndex: item.json.promptIndex,
    prompt: item.json.prompt,
    theme_name: item.json.theme_name,
    session_id: item.json.session_id,
    customer_name: item.json.customer_name,
    petKind: item.json.petKind,
    petname: item.json.petname
  }
}));

// 1. Grab both pieces of JSON from the merged node:
const inputItems = $input.all();

// 2. Find the total count and the rejected array:
let totalCount = 0;
let rejected = [];

for (const it of inputItems) {
  if (it.json.totalCount !== undefined) {
    totalCount = it.json.totalCount;
  }
  if (Array.isArray(it.json.rejected)) {
    rejected = it.json.rejected;
  }
}

// 3. Compute the accepted indices:
const accepted = [];
for (let i = 1; i <= totalCount; i++) {
  if (!rejected.includes(i)) {
    accepted.push(i);
  }
}

// 4. Emit one item *per* accepted index:
return accepted.map(idx => ({
  json: { promptIndex: idx }
}));
