  for (const key in item.binary) {
    binaryData[key] = {...item.binary[key]};

    // Ensure all jpg images use the correct mime type
    if (binaryData[key].mimeType === 'image/jpg') {
      binaryData[key].mimeType = 'image/jpeg';
    }
  }
