// These are the outputs of the 'Prepare Cloudinary Upload' node from its last full run.
const allOriginalDetailsItems = $('Prepare Cloudinary Upload1').all();

if (cloudinaryResponses.length === 0) {
  console.warn("Combine Cloudinary: No Cloudinary responses received.");
  return [];
}
if (allOriginalDetailsItems.length === 0) {
