async function getImageSize(imageUrl) {
  try {
    const response = await fetch(imageUrl, { method: 'HEAD' });
    const contentLength = response.headers.get('content-length');

    if (contentLength) {
      const sizeBytes = parseInt(contentLength);
      const sizeMB = sizeBytes / (1024 * 1024);
      return {
        sizeBytes: sizeBytes,
        sizeMB: Math.round(sizeMB * 100) / 100, // Round to 2 decimals
        sizeKB: Math.round(sizeBytes / 1024),
        needsCompression: sizeMB > 3
      };
    } else {
      // Fallback: Download a small portion to estimate size
      const partialResponse = await fetch(imageUrl, { 
        headers: { 'Range': 'bytes=0-1023' } // First 1KB
      });

      if (partialResponse.status === 206) { // Partial content
        const contentRange = partialResponse.headers.get('content-range');
        const totalSize = parseInt(contentRange.split('/')[1]);
        const sizeMB = totalSize / (1024 * 1024);

        return {
          sizeBytes: totalSize,
          sizeMB: Math.round(sizeMB * 100) / 100,
          sizeKB: Math.round(totalSize / 1024),
          needsCompression: sizeMB > 3
        };
      }

      // If we can't determine size, assume it needs compression to be safe
      return {
        sizeBytes: 0,
        sizeMB: 0,
        sizeKB: 0,
        needsCompression: true
      };
    }

  } catch (error) {
    console.error(`Error checking size for ${imageUrl}:`, error);
    // If error, assume it needs compression to be safe
    return {
      sizeBytes: 0,
      sizeMB: 0,
      sizeKB: 0,
      needsCompression: true
    };
  }
}

