          sizeBytes: totalSize,
          sizeMB: Math.round(sizeMB * 100) / 100,
          sizeKB: Math.round(totalSize / 1024),
          needsCompression: sizeMB > 3
        };
      }

      // If we can't determine size, assume it needs compression to be safe
      return {
        sizeBytes: 0,
        sizeMB: 0,
        sizeKB: 0,
        needsCompression: true
      };
    }

  } catch (error) {
    console.error(`Error checking size for ${imageUrl}:`, error);
    // If error, assume it needs compression to be safe
    return {
      sizeBytes: 0,
      sizeMB: 0,
      sizeKB: 0,
      needsCompression: true
    };
  }
}

// Process all items
const processedItems = [];

for (const item of items) {
  console.log(`Checking size for: ${item.json.imageName || 'image'}`);

  const sizeInfo = await getImageSize(item.json.imageUrl);

  console.log(`Image size: ${sizeInfo.sizeMB}MB (${sizeInfo.sizeKB}KB) - Compression needed: ${sizeInfo.needsCompression}`);

  processedItems.push({
    json: {
      ...item.json,
      imageSize: sizeInfo
    }
  });
}

return processedItems;

// The input to this node comes from the "Image validation agent".
// Its input, in turn, came from "Prepare Image Payload".
// We need to grab that original data.
const originalItem = $('Prepare Image Payload').first();

// Now, parse the new validation result from the agent.
const validationResult = JSON.parse($input.first().json.output);

// Return a single item that contains BOTH the original data
// (including the precious binary files) AND the new validation result.
return [{
  json: {
    ...originalItem.json, // Pass through all original JSON data
    validationResults: validationResult
  },
  binary: originalItem.binary // CRITICAL: Pass through the binary data
}];

// Get the validation results from the input JSON
const results = $input.item.json.validationResults;

// Initialize an empty array to hold the reasons for failure
const reasons = [];

// Check each validation rule and add a user-friendly message if it failed
if (results.isPetPresent === false) {
  reasons.push("we could not detect a pet in the images provided");
}

if (results.isSingleSubject === false) {
  reasons.push("more than one pet or person was detected in the images");
}

if (results.isSafeForWork === false) {
  reasons.push("the images were flagged for containing content that violates our safety policy");
}

// Format the final reason string.
let finalReason = "Your image submission could not be processed because ";

if (reasons.length > 1) {
  // If there are multiple reasons, join them nicely.
  // Example: "reason 1 and reason 2"
  finalReason += reasons.slice(0, -1).join(', ') + ' and ' + reasons.slice(-1);
} else {
  // If there's only one reason.
  finalReason += reasons[0];
}

finalReason += ". Please re-upload your photos, ensuring they meet our guidelines.";

// Return the final reason in a new JSON object
// This object will be passed to the 'Send Email' node.
return {
  json: {
    rejectionReason: finalReason
  }
};

