// In your "prepare cloudinary upload" Code Node

const allItems = $input.all();

// 1. Find the single customer data item from the current input.
const userDataItem = allItems.find(item => item.json.email);
if (!userDataItem) {
  throw new Error("Could not find customer data item in the input.");
}
const customerData = userDataItem.json;

// 2. Find all items that have a generated image (binary data).
const imageItems = allItems.filter(item => item.binary && Object.keys(item.binary).length > 0);
if (imageItems.length === 0) {
    throw new Error("No items with generated images were found in the input.");
}

// 3. --- THIS IS THE KEY ---
// Fetch the original prompt metadata directly from the "distribute images" node.
// This data was lost during image generation, so we retrieve it from its source.
//
// Important: Replace 'distribute images' if your node has a different name.
const promptMetadataItems = $('Distribute_images').all();
if (promptMetadataItems.length === 0) {
  throw new Error("Could not retrieve metadata from the 'distribute images' node. Check the node name.");
}

// 4. Map over each image and pair it with its original metadata by its order (index).
return imageItems.map((imageItem, index) => {
  // The items from "distribute images" are in the same order as the generated images.
  // We use the index to find the correct metadata for the current image.
  const matchingPromptData = promptMetadataItems[index].json;

  // Now we can reliably get the promptIndex and theme_name from the metadata we just fetched.
  const promptIndex = matchingPromptData.promptIndex;
  const themeName = matchingPromptData.theme_name;

  // Create the unique filename using the correct promptIndex.
  const customFilename = `${customerData.session_id}_prompt${promptIndex}`;

  // Build the final JSON object for this item.
  return {
    json: {
      // Copy all fields from the customer data object
        session_id: customerData.session_id,
        email: customerData.email,
        petname: customerData.petname,
        petKind: customerData.petKind,
        customer_name: customerData.customer_name,
      // Add the data specific to this prompt and image
      promptIndex: promptIndex,
      theme_name: themeName,
      customFilename: customFilename
    },
    // Pass along the binary data for this specific image.
    binary: imageItem.binary
  };
});
