// --- Error Checking --
if (!productInfo || !productInfo.product_title) {
  throw new Error("Could not find original product data (like product_title) from the loop.");
}
if (!uploadedImage || !uploadedImage.id) {
  throw new Error("Could not find a valid image ID from the upload step.");
}
if (!providerVariantsResponse || !providerVariantsResponse.variants) {
  throw new Error("Could not find a valid variants array from the API call.");
}


// --- Dynamically extract placeholder position from provider response ---
const placeholderPosition = providerVariantsResponse.variants?.[0]?.print_areas?.[0]?.placeholders?.[0]?.position || "front";

// --- UPDATED LOGIC STARTS HERE --

// Get the list of variant IDs we passed from the "Map_Printify_Products" node
const requiredVariantIds = productInfo.required_variant_ids;
if (!requiredVariantIds || !Array.isArray(requiredVariantIds)) {
  throw new Error("Could not find the 'required_variant_ids' array. Check the 'Map_Printify_Products' node.");
}

// Filter the variants from the API response to only include the ones we want
const variants = providerVariantsResponse.variants
  .filter(variant => requiredVariantIds.includes(variant.id))
  .map(variant => ({
    id: variant.id,
    price: 2999, // You can keep this or adjust as needed
    is_enabled: true
  }));

if (variants.length === 0) {
    throw new Error(`Could not find any matching variants for the provided IDs: ${requiredVariantIds.join(', ')}`);
}

const all_variant_ids = variants.map(v => v.id);

// --- UPDATED LOGIC ENDS HERE ---

const payload = {
  "title": productInfo.product_title,
  "description": `<p>Custom ${productInfo.theme_name} artwork featuring ${productInfo.petname}. High-quality print-on-demand product perfect for pet lovers.</p>`,
  "blueprint_id": productInfo.blueprint_id,
  "print_provider_id": productInfo.print_provider_id,
  "variants": variants,
  "print_areas": [
    {
      "variant_ids": all_variant_ids,
      "placeholders": [
        {
          "position": placeholderPosition,
          "images": [
            {
              "id": uploadedImage.id,
              "x": 0.5,
              "y": 0.5,
              "scale": 1,
              "angle": 0
            }
          ]
        }
      ]
    }
  ],
  // Add your tags here
  "tags": [
    $('When Executed by Another Workflow').first().json.email,
    $('When Executed by Another Workflow').first().json.petGender,
    $('When Executed by Another Workflow').first().json.petKind,
    $('When Executed by Another Workflow').first().json.session_id,
    productInfo.theme_name // <-- ADD THIS LINE
  ]
};

// Return the final data
return {
  json: {
    ...productInfo,
    printifyPayload: payload
  }
};

// Node: Collect All Products (FIXED)
const allItems = $input.all();
const shopifyCollectionsMap = new Map();
const productsToProcess = [];

console.log(`Collect All Products - Received ${allItems.length} items.`);

// --- First Pass: Map all Shopify collections by their title ---
allItems.forEach((item) => {
  // Identify collection items (they have a collection_id but no product id)
  if (item.json.collection_id && !item.json.id) {
    const collectionTitle = item.json.title;
    if (collectionTitle && !shopifyCollectionsMap.has(collectionTitle)) {
      shopifyCollectionsMap.set(collectionTitle, {
        collection_id: item.json.collection_id,
        collection_handle: item.json.handle,
      });
      console.log(`Mapped Shopify Collection: "${collectionTitle}" -> ID: ${item.json.collection_id}`);
    }
  }
});

// --- Pre-computation: Find the pet name for this batch ---
const referenceItem = allItems.find(item => item.json.petname);
const petnameForBatch = referenceItem ? referenceItem.json.petname : null;

if (!petnameForBatch) {
  console.error("CRITICAL: Could not determine pet name for this batch. Processing will likely fail.");
  return [];
}

// --- Second Pass: Match products to their collections ---
allItems.forEach((item) => {
  // Identify product items (they have a product ID, tags, and a title)
  if (item.json.id && item.json.tags && item.json.title && item.json.title.includes(' - ')) {
    const productTitle = item.json.title;

    // 1. Extract the theme name from the product title.
    // e.g., "Spa Day Deluxe - Premium T-Shirt" -> "Spa Day Deluxe"
    const productTheme = productTitle.split(' - ')[0].trim();

    // 2. Construct the expected Shopify collection title.
    // e.g., "Milo" + " " + "Spa Day Deluxe" -> "Milo Spa Day Deluxe"
    const collectionTitleKey = `${petnameForBatch} ${productTheme}`;

    // 3. Look up the collection in our map.
    const collectionData = shopifyCollectionsMap.get(collectionTitleKey);

    if (collectionData) {
      // Find the session_id from the tags (assuming it's the 4th tag)
      const sessionIdTag = item.json.tags && item.json.tags.length > 3 ? item.json.tags[3] : null;
      if (!sessionIdTag) {
        console.warn(`Could not find a session_id tag for product: "${productTitle}". Skipping.`);
        return; // Skips this product
      }

      productsToProcess.push({
        json: {
          product_title_to_search_in_shopify: productTitle,
          shopify_collection_id: collectionData.collection_id,
          shopify_collection_handle: collectionData.collection_handle,
          shopify_collection_original_title: collectionTitleKey,
          session_id_to_match: sessionIdTag,
          gelato_product_id: item.json.id // Note: This field name is from your original code
        }
      });
      console.log(`SUCCESS: Matched product "${productTitle}" to collection "${collectionTitleKey}"`);
    } else {
      console.warn(`FAIL: No Shopify collection found for product: "${productTitle}". (Used key: "${collectionTitleKey}"). This product will be skipped.`);
    }
  }
});

