const blueprintId = productInfo.blueprint_id;
const mockupStyle = productInfo.mockup_style; // Get the style key
const requiredVariantIds = productInfo.required_variant_ids;

const newProductId = createdProduct.id;
const curatedImageUrls = [];

// First, check if there's a detailed, variant-specific recipe for this style
if (mockupStyle && variantSpecificMockups[mockupStyle]) {
  console.log(`üöÄ Using detailed variant-specific logic for style: ${mockupStyle}`);
  const config = variantSpecificMockups[mockupStyle];
  const templateName = config.template_name;
  const enabledVariants = createdProduct.variants.filter(v => requiredVariantIds.includes(v.id));

  // --- Logic to prioritize the iPhone 16 Pro variant ---

  // Set the confirmed variant ID for the iPhone 16 Pro as the primary one.
  const primaryVariantId = '112191'; 

  // Separate the primary variant from the others
  const primaryVariant = enabledVariants.find(v => v.id == primaryVariantId);
  const otherVariants = enabledVariants.filter(v => v.id != primaryVariantId);

  // Helper function to add mockups to the array to avoid repeating code
  const addMockupsForVariant = (variant) => {
    if (!variant) return;
    const mockupsForThisVariant = config.variants[variant.id];
