      }
    }
  } else {
    const defaultVariant = createdProduct.variants.find(v => v.is_default);
    if (!defaultVariant) { throw new Error(`Could not find a default variant for product ${newProductId}`); }
    const referenceVariantId = defaultVariant.id;
