// Function to extract data from collection
function extractDataFromCollection(collection) {
  console.log('=== EXTRACTING DATA FROM COLLECTION ===');
  console.log('Collection ID:', collection.id);
  console.log('Collection Title:', collection.title);
  console.log('Collection Handle:', collection.handle);
  console.log('Collection Image URL:', collection.image?.src);

  // Initialize variables
  let session_id = "";
  let promptIndex = 1;

  // FIXED: Extract from Shopify CDN URLs
  if (collection.image && collection.image.src) {
    console.log('Attempting to extract from image URL:', collection.image.src);

    // Updated regex for Shopify CDN URLs - try multiple patterns
    let urlMatch = collection.image.src.match(/collections\/([a-f0-9]+)_prompt(\d+)\.(png|jpg|jpeg|webp)/);
    if (!urlMatch) {
      // Try without collections/ prefix
      urlMatch = collection.image.src.match(/([a-f0-9]+)_prompt(\d+)\.(png|jpg|jpeg|webp)/);
    }
    if (!urlMatch) {
      // Try without file extension
      urlMatch = collection.image.src.match(/([a-f0-9]+)_prompt(\d+)/);
    }

    if (urlMatch) {
      session_id = urlMatch[1];
      promptIndex = parseInt(urlMatch[2]);
      console.log('✓ Successfully extracted from URL - Session ID:', session_id, 'Prompt Index:', promptIndex);
    } else {
      console.log('❌ Failed to extract from image URL');
    }
  }

  // Fallback 1: Extract session_id from collection handle
