// Node: Collect All Products (FIXED)

// Get all items from the previous node
const allItems = $input.all();
const shopifyCollectionsMap = new Map(); // Key: "PetName ThemeName", Value: { collection_id, handle, title }
const productsToProcess = [];

console.log(`Collect All Products - Received ${allItems.length} items.`);

// --- Pre-computation: Find the pet name for this batch ---
const referenceItemForPetName = allItems.find(item => item.json && item.json.collection_id && item.json.petname);
const petnameForBatch = referenceItemForPetName ? referenceItemForPetName.json.petname : null;

if (!petnameForBatch) {
  console.error("CRITICAL: Could not determine pet name for this batch. No item with a 'petname' property was found. Make sure the 'gather collections' node is setting it correctly. Processing will stop.");
  return []; // Stop if we can't find the pet name
}
console.log(`Determined Pet Name for this batch: \"${petnameForBatch}\"`);

// --- First Pass: Populate shopifyCollectionsMap from Shopify Collection Items ---
allItems.forEach((item) => {
  // Shopify Collection items have collection_id and their title is "PetName ThemeName"
  if (item.json.collection_id && item.json.title && typeof item.json.title === 'string' && !item.json.id) {
    const shopifyCollectionTitleKey = item.json.title;
    if (!shopifyCollectionsMap.has(shopifyCollectionTitleKey)) {
      shopifyCollectionsMap.set(shopifyCollectionTitleKey, {
        collection_id: item.json.collection_id,
        collection_handle: item.json.handle,
        original_shopify_collection_title: item.json.title
      });
      console.log(`Mapped Shopify Collection: \"${shopifyCollectionTitleKey}\" -> ID: ${item.json.collection_id}`);
    }
  }
});

if (shopifyCollectionsMap.size === 0) {
  console.warn("No Shopify collections were identified. Downstream processing will likely fail.");
} else {
  console.log(`Total Shopify collections mapped: ${shopifyCollectionsMap.size}`);
}

// --- Second Pass: Match Gelato products to Shopify collections ---
allItems.forEach((item) => {
  // Gelato product items have an 'id' and a title with ' - '
  if (item.json.id && item.json.tags && Array.isArray(item.json.tags) && item.json.tags.length > 0 && item.json.title && item.json.title.includes(' - ')) {
    const gelatoProductTitle = item.json.title;

    // 1. DERIVE the theme name part from the product's own title.
    // e.g., "Cat Spa Day Deluxe - Premium Cotton..." -> "Cat Spa Day Deluxe"
    const derivedThemeNameWithKind = gelatoProductTitle.split(' - ')[0].trim();

    // 2. CONSTRUCT the Shopify collection key correctly
    // The key in the map is "{Pet Name} {Theme}", e.g. "Renato Cat Spa Day Deluxe"
    // We need to build this from `petnameForBatch` ("Renato Cat") and the actual theme ("Spa Day Deluxe")
    const petKind = item.json.tags[2]; // Assumes petKind is the 3rd tag, e.g., "Cat"

    // Remove the pet kind from the derived theme to get the base theme name
    const themeWithoutKind = derivedThemeNameWithKind.replace(petKind, '').trim(); // e.g., "Spa Day Deluxe"

    // Construct the correct key
    const shopifyCollectionTitleKey = `${petnameForBatch} ${themeWithoutKind}`; // e.g., "Renato Cat Spa Day Deluxe"

    // 3. LOOK UP the collection data in our map.
    const collectionData = shopifyCollectionsMap.get(shopifyCollectionTitleKey);

    if (collectionData) {
      const session_id_tag = item.json.tags[0]; // Assumes session_id is the first tag
      if (!session_id_tag) {
          console.warn(`Could not find a session_id tag for product: \"${gelatoProductTitle}\". Skipping.`);
          return; // Skips this iteration
      }

      productsToProcess.push({
        json: {
          product_title_to_search_in_shopify: gelatoProductTitle,
          shopify_collection_id: collectionData.collection_id,
          shopify_collection_handle: collectionData.collection_handle,
          shopify_collection_original_title: collectionData.original_shopify_collection_title,
          session_id_to_match: session_id_tag,
          gelato_product_id: item.json.id
        }
      });
      console.log(`SUCCESS: Matched product \"${gelatoProductTitle}\" to collection \"${shopifyCollectionTitleKey}\"`);
    } else {
      console.warn(`FAIL: No Shopify collection found for product: \"${gelatoProductTitle}\". (Used key: \"${shopifyCollectionTitleKey}\"). This product will be skipped.`);
    }
  }
});

if (productsToProcess.length === 0) {
  console.warn("No Gelato products were successfully matched to Shopify collections.");
}

console.log(`Total products to process: ${productsToProcess.length}`);
return productsToProcess;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

const currentProductData = $input.item.json;
return {
  json: {
    product_title: currentProductData.product_title_to_search_in_shopify,
    shopify_collection_id: currentProductData.shopify_collection_id,

    // --- ADD THIS LINE ---
    session_id_to_match: currentProductData.session_id_to_match, // Pass it through

    // Context fields (no changes needed here)
    original_search_title: currentProductData.product_title_to_search_in_shopify,
    original_shopify_collection_title: currentProductData.shopify_collection_original_title,
    gelato_product_id_context: currentProductData.gelato_product_id
  }
};

// Get the API response from SearchShopifyProduct
const shopifyApiResponse = $input.item.json;

// Get the data sent *into* SearchShopifyProduct
const dataFromPreviousNode = $('Format Product Search Data').item.json;

let foundProduct = null;
const allFoundProducts = shopifyApiResponse.products || [];
const targetSessionId = dataFromPreviousNode.session_id_to_match;
const searchedTitle = dataFromPreviousNode.product_title;

console.log(`--- Starting Product ID Extraction ---`);
console.log(`Target Session ID: "${targetSessionId}"`);
console.log(`Searching for product title: "${searchedTitle}"`);

if (!targetSessionId) {
  console.error("CRITICAL ERROR: No session_id was passed to this node. Cannot perform matching. Check the 'Collect All Products' node.");
  // FIXED: Return a single object, not an array
  return { json: { product_id: null, error: "Missing session_id_to_match" } };
}

if (allFoundProducts.length === 0) {
  console.warn(`Shopify search returned ZERO products for title "${searchedTitle}".`);
} else {
  console.log(`Shopify search returned ${allFoundProducts.length} product(s). Now filtering...`);

  const lowerCaseTargetSessionId = targetSessionId.toLowerCase();

  for (const product of allFoundProducts) {
    const shopifyTagsString = product.tags;

    if (shopifyTagsString) {
      const lowerCaseShopifyTags = shopifyTagsString.toLowerCase();
      console.log(`- Checking Product ID: ${product.id}. Lowercase Tags: "${lowerCaseShopifyTags}". Does it include "${lowerCaseTargetSessionId}"?`);
      if (lowerCaseShopifyTags.includes(lowerCaseTargetSessionId)) {
        foundProduct = product;
        console.log(`✅ MATCH FOUND! Using Product ID: ${foundProduct.id}`);
        break;
      }
    } else {
      console.log(`- Checking Product ID: ${product.id}. No tags found.`);
    }
