// In "Split_Product_Types" (ID: d1bb84ef-a798-4c7b-8c15-64726de190ca)
const items = $input.all(); // Items from "Merge Customer With Transformations"
const outputItems = [];

// Customer data (item without asset_id, usually from Extract Parameters via the merge)
const customerDataItem = items.find(item => item.json && item.json.email && !item.json.asset_id && !item.json.public_id);
// Image items (items with asset_id/public_id from Transformation node via the merge)
const imageItems = items.filter(item => item.json && (item.json.asset_id || item.json.public_id));

if (!customerDataItem) {
  console.error("Split_Product_Types: Customer data not found. Ensure 'Merge Customer With Transformations' is correctly merging.");
  return [{ json: { error: "Customer data item not found." } }];
}
const customerData = customerDataItem.json;

if (imageItems.length === 0) {
  console.warn("Split_Product_Types: No image items found to process.");
}

imageItems.forEach(item => {
  const imageJson = item.json; // This is the rich data from the Transformation node

  // promptIndex and theme_name should now be directly available in imageJson
  const promptIndex = imageJson.promptIndex;
  const actualThemeName = imageJson.theme_name;

  if (promptIndex === undefined || !actualThemeName) {
    console.error(`Split_Product_Types: Missing promptIndex or theme_name for image item. Public ID: ${imageJson.public_id}. Skipping.`);
    return; // Skip this item
  }

  // Use petname from customerData (more reliable) or fallback to imageJson if it somehow has it
  const petName = customerData.petname || imageJson.petname || "Pet";
  const petKind = customerData.petkind || imageJson.petkind || "Pet";
  const petGender = $input.first().json.petGender|| imageJson.petgender || "Pet";
  const sessionId = customerData.session_id || imageJson.session_id || Date.now().toString().slice(-8);

  const baseHandle = `${petName.toLowerCase().replace(/\s+/g, '-')}-${actualThemeName.toLowerCase().replace(/[^a-z0-9-]+/g, '').replace(/\s+/g, '-')}`;
  const sessionSuffix = sessionId.slice(-8);
  const uniqueHandle = `${baseHandle}-${sessionSuffix}`;

  // Use spread operator to preserve original field order, then override specific fields
  outputItems.push({
    json: {
      // Start with customer data
      session_id: sessionId,
      email: customerData.email || imageJson.email,
      petname: petName,
      petGender: petGender,
      petKind: customerData.petKind || imageJson.petKind,
      customer_name: customerData.customer_name || imageJson.customer_name,

      // Include all cloudinary data (preserves original order)
      ...imageJson,

      // Override/ensure these specific fields are set correctly
      promptIndex: promptIndex,
      collection_name: `${petName} ${actualThemeName}`,
      collection_handle: uniqueHandle,
      theme_name: actualThemeName,
      collection_image_url: imageJson.original_url // Use original URL for collection image
    }
  });
});

return outputItems;

