// In "Map_Printify_Products"
const items = $input.all();
const outputItems = [];

// We will ONLY process the Matte Canvas which we know works.
const printifyProducts = {
  "other": [
    { 
      blueprint_id: 937, 
      print_provider_id: 105, 
      name: "Matte Canvas" 
    }
  ]
};

// Get all the source data from the first node one time
const allSourceData = $('Split_Product_Types').all();

// Process each collection item from the "Create_shopify_collection1" node
items.forEach(item => {
  const collection = item.json.custom_collection;

  // Find the original data by matching the collection title
  const sourceDataItem = allSourceData.find(
    sourceItem => sourceItem.json.collection_name === collection.title
  );

  if (collection && sourceDataItem) {
    const sourceData = sourceDataItem.json;
    // const imageUrl = collection.image.src;
    const imageUrl = "https://res.cloudinary.com/myfurryprints/image/upload/e_upscale/c_scale,w_4000/l_Mask3larger_jpm63l,w_4000,fl_cutter/6876a52bd16692fc1b0f9a38/6876a52bd16692fc1b0f9a38_prompt1";

    // Create "other" products (the Canvas)
    printifyProducts.other.forEach(product => {
      outputItems.push({
        json: {
          session_id: sourceData.session_id,
          email: sourceData.email,
          petname: sourceData.petname,
          customer_name: sourceData.customer_name,
          promptIndex: sourceData.promptIndex,
          theme_name: sourceData.theme_name,
          shopify_collection_id: collection.id,
          product_type: "other",
          image_url: imageUrl,
          blueprint_id: product.blueprint_id,
          print_provider_id: product.print_provider_id,
          product_title: `${sourceData.theme_name} - ${product.name}`,
          template_name: product.name
        }
      });
    });
  }
});

return outputItems;

// This code is designed for the SEQUENTIAL flow.

// 1. Get the original data from the start of the loop
const productInfo = $('Loop Product Templates').item.json;

// 2. Get the result of the image upload
const uploadedImage = $('Upload_Image_To_Printify').item.json;

// 3. Get the result of the variants API call (this is the direct input)
const providerVariantsResponse = $input.item.json;

// --- Error Checking ---
if (!productInfo || !productInfo.product_title) {
  throw new Error("Could not find original product data (like product_title) from the loop.");
}
if (!uploadedImage || !uploadedImage.id) {
  throw new Error("Could not find a valid image ID from the upload step.");
}
if (!providerVariantsResponse || !providerVariantsResponse.variants) {
  throw new Error("Could not find a valid variants array from the API call.");
}

// --- Payload Assembly ---
const variants = providerVariantsResponse.variants.map(variant => ({
  id: variant.id,
  price: 2999, // Price in cents, e.g., 2999 = $29.99
  is_enabled: true
}));

const all_variant_ids = variants.map(v => v.id);

const payload = {
  "title": productInfo.product_title,
  "description": `<p>Custom ${productInfo.theme_name} artwork featuring ${productInfo.petname}. High-quality print-on-demand product perfect for pet lovers.</p>`,
  "blueprint_id": productInfo.blueprint_id,
  "print_provider_id": productInfo.print_provider_id,
  "variants": variants,
  "print_areas": [
    {
      "variant_ids": all_variant_ids,
      "placeholders": [
        {
          "position": "front",
          "images": [
            {
              "id": uploadedImage.id,
              "x": 0.5,
              "y": 0.5,
              "scale": 1,
              "angle": 0
            }
          ]
        }
      ]
    }
  ]
};

// Return the final data
return {
  json: {
    ...productInfo,
    printifyPayload: payload
  }
};

