parameters.jsCode:
const pageResponse = $('Create Gallery Page').all()[0].json;
const config = $('Set Configuration').all()[0].json;
const processedData = $('Process Collections').all()[0].json;

// Check if we have a successful response (201 Created)
if (pageResponse.statusCode === 201 && pageResponse.body && pageResponse.body.page) {
  const page = pageResponse.body.page;
  const pageUrl = `https://${config.shopify_store_url.replace('.myshopify.com', '')}.myshopify.com/pages/${page.handle}`;

  return {
    success: true,
    page_id: page.id,
    page_handle: page.handle,
    page_url: pageUrl,
    admin_url: `https://${config.shopify_store_url}/admin/pages/${page.id}`,
    collections_included: processedData.collections.map(c => ({
      handle: c.handle,
      title: c.title,
      products_count: c.products_count
    })),
    gallery_title: processedData.gallery_title,
    metafields_set: [
      'custom.gallery_title',
      'custom.gallery_description',
      'custom.collection_ids'
    ],
    created_at: new Date().toISOString()
  };
} else {
  throw new Error(`Failed to create page: Status ${pageResponse.statusCode} - ${pageResponse.statusMessage}`);
}

const config = $input.item.json;

// ✅ Ensure we have a valid collectionHandlesString
if (!config.collectionHandlesString || typeof config.collectionHandlesString !== 'string') {
  throw new Error("Missing or invalid 'collectionHandlesString'");
}

// ✅ Build minimal fake 'collections' array just from handles
const handles = config.collectionHandlesString
  .split(',')
  .map(h => h.trim())
  .filter(h => h.length > 0);

// You can optionally attach placeholder metadata if needed later
const collections = handles.map(h => ({
  handle: h,
  title: h.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase()), // e.g., \"sammy-spa-day\" → \"Sammy Spa Day\"
  products_count: 1
}));

// --- CORRECTED: Include petname and ensure gallery_description is passed through ---
const pageData = {
  collections,
  collection_handles: handles.join(','),
  gallery_title: config.gallery_title || 'My Art Gallery', // Keep this for compatibility if needed later, but API call ignores it
  gallery_description: config.gallery_description || 'Enjoy your custom artwork!', // Pass through description from input
  page_handle: 'myfurryprints-' + Date.now().toString().slice(-4),
  petname: config.petname // <--- ADDED: Include petname from input
};

return [{ json: pageData }];

// Node Name: Extract Merged Data
// Receives the array of items from the Merge node output.
// Finds and consolidates collection handles string, email, and petname.
// Outputs a single item with the consolidated data.

const items = $input.all(); // Get all items from the merge node output

// Initialize variables to store the data we need to extract
let collectionHandlesString = '';
let email = '';
let petname = '';

// Loop through each item produced by the merge node
items.forEach(item => {
  // Check if the current item has the 'collectionHandlesString' property
  if (item.json && item.json.collectionHandlesString) {
    collectionHandlesString = item.json.collectionHandlesString;
    // Optional: Log that we found it
    console.log('Found collectionHandlesString:', collectionHandlesString);
  }

  // Check if the current item has the 'email' property (assuming email and petname are in the same item)
  if (item.json && item.json.email) {
    email = item.json.email;
    petname = item.json.petname || ''; // Also get petname from the same item
    // Optional: Log that we found user data
    console.log('Found user data (email, petname):', email, petname);
  }

  // We don't need to process the items with the 'collect' object for this specific task.
});

// Check if essential data was found (optional but recommended)
if (!collectionHandlesString || !email || !petname) {
    console.warn('One or more required fields not found in merge output:', {
        collectionHandlesString: collectionHandlesString ? 'Found' : 'Not Found',
        email: email ? 'Found' : 'Not Found',
        petname: petname ? 'Found' : 'Not Found'
    });
    // You might choose to throw an error here if the data is critical,
    // or return an item indicating failure, or return with empty values.
    // For now, we proceed with whatever was found.
}


// Return a single item containing the consolidated data
return [{
  json: {
    collectionHandlesString: collectionHandlesString,
    email: email,
    petname: petname
  }
}];

