// This node takes the response from the Printify product creation
// and constructs a curated list of mockup URLs based on a predefined map.

// --- CONFIGURATION: The Mockup Map ---
// You must manually find these values from your Printify templates
// for every product blueprint you want to support.
const mockupMap = {
  // Key: blueprint_id
  12: { // Bella + Canvas 3001 (Unisex T-shirt)
    // List of mockups you want for this product.
    // The "id" is the magic number from the URL.
    mockups: [
      { id: 92584, camera_label: 'lifestyle' },
      { id: 102044, camera_label: 'front-2' },
      { id: 102050, camera_label: 'size-chart' }
    ],
    // The base name for the image file in the URL
    template_name: 'template-premium-cotton-unisex-t-shirt'
  },
  // Add other blueprints here, for example:
  // 635: { // Color Accent Mug
  //   mockups: [ ... ],
  //   template_name: 'template-color-accent-mug'
  // }
};

const createdProduct = $input.item.json;
const blueprintId = $('Prepare_Printify_Product_Payload').item.json.blueprint_id;

// Find the mockup configuration for this blueprint
const config = mockupMap[blueprintId];
if (!config) {
  console.warn(`⚠️ No mockup configuration found for blueprint_id: ${blueprintId}. No curated mockups will be generated.`);
  // Pass through the data without curated URLs
  return [{ json: { ...createdProduct, curatedImageUrls: [] } }];
}

const newProductId = createdProduct.id;
const curatedImageUrls = [];

// We only need one variant ID to generate the common mockups
// Printify often uses the "default" variant for this.
const defaultVariant = createdProduct.variants.find(v => v.is_default);
if (!defaultVariant) {
  throw new Error(`Could not find a default variant for product ${newProductId}`);
}
const referenceVariantId = defaultVariant.id;

// Loop through our desired mockups and build the URLs
for (const mockup of config.mockups) {
  const imageUrl = `https://images.printify.com/mockup/${newProductId}/${referenceVariantId}/${mockup.id}/${config.template_name}.jpg?camera_label=${mockup.camera_label}`;
  curatedImageUrls.push(imageUrl);
  console.log(`✅ Constructed URL: ${imageUrl}`);
}

// Pass along the original data from the creation step,
// but add our new array of curated mockup URLs.
return [{
  json: {
    ...createdProduct,
    curatedImageUrls: curatedImageUrls
  }
}];

// Get the single item from the Merge node
const inputData = $input.item.json;

// 1. Parse the string of URLs into a real JavaScript array
const curatedImageUrls = JSON.parse(inputData.curated_image_url);

// 2. Extract the GraphQL Product ID
const productNode = inputData.data.products.edges[0]?.node;
if (!productNode) {
  throw new Error("Product not found in the data from the search node. Cannot proceed.");
}
const graphQlProductId = productNode.id;

// 3. Create the 'product' variable object
const productVariable = {
  id: graphQlProductId
};

// 4. Create the 'media' variable array, using the correct key "originalSource"
const mediaVariable = curatedImageUrls.map(url => ({
  originalSource: url,
  mediaContentType: 'IMAGE'
}));

// 5. Return a final object that has two top-level keys: "product" and "media"
return {
  json: {
    product: productVariable,
    media: mediaVariable
  }
};

