    mockups: [
      { id: 93457, camera_label: 'front' } // Example Mockup ID, replace with a real one
    ]
  }
};

// --- 2. LOGIC TO CONSTRUCT URLs ---

const createdProduct = $input.item.json;
const blueprintId = createdProduct.blueprint_id;
const config = mockupMap[blueprintId];

if (!config) {
  console.warn(`⚠️ No mockup configuration found for blueprint_id: ${blueprintId}. No curated mockups will be generated.`);
  // Return the input but with an empty stringified array for the URLs
  return { json: { ...createdProduct, curated_image_url: JSON.stringify([]) } };
}

const newProductId = createdProduct.id;
const curatedImageUrls = [];

if (config.generatePerVariant) {
  // Logic for products like mugs and canvases where each variant has a unique mockup
  for (const variant of createdProduct.variants) {
    for (const mockup of config.mockups) {
      // CORRECTED URL FORMAT
      const imageUrl = `https://images.printify.com/mockup/${newProductId}/${variant.id}/${mockup.id}/${config.template_name}.jpg?camera_label=${mockup.camera_label}`;
      curatedImageUrls.push(imageUrl);
    }
  }
} else {
  // Logic for products like T-shirts where mockups are shared across variants
  const defaultVariant = createdProduct.variants.find(v => v.is_default);
  if (!defaultVariant) {
    throw new Error(`Could not find a default variant for product ${newProductId}`);
  }
  const referenceVariantId = defaultVariant.id;
  for (const mockup of config.mockups) {
    // CORRECTED URL FORMAT
    const imageUrl = `https://images.printify.com/mockup/${newProductId}/${referenceVariantId}/${mockup.id}/${config.template_name}.jpg?camera_label=${mockup.camera_label}`;
    curatedImageUrls.push(imageUrl);
  }
}

// Deduplicate the URLs just in case
const uniqueUrls = [...new Set(curatedImageUrls)];

// Pass along the original data, but add our new array of URLs (as a JSON string)
return {
  json: {
    ...createdProduct,
    curated_image_url: JSON.stringify(uniqueUrls)
  }
};

