const uploadResult = $input.item.json.processedProductData;
const shopifyProduct = JSON.parse($input.item.json.shopifyProduct);
const printifyProduct = JSON.parse($input.item.json.printifyProduct);
const allCuratedImageUrls = JSON.parse($input.item.json.curated_image_urls);
const blueprintId = printifyProduct.blueprint_id;

// --- ERROR HANDLING ---
if (!printifyProduct || !shopifyProduct || !uploadResult || !allCuratedImageUrls) {
  throw new Error("Could not find required data after the final merge.");
}

const uploadedMediaEdges = uploadResult.media.edges;

// --- SKU-BASED MAPPING LOGIC ---

const shopifyMediaMap = new Map();
for (let i = 0; i < uploadedMediaEdges.length; i++) {
  if (allCuratedImageUrls[i]) {
    // This is the Shopify Media GID (e.g., "gid://shopify/MediaImage/...")
    shopifyMediaMap.set(allCuratedImageUrls[i], uploadedMediaEdges[i].node.id);
  }
}

const shopifyVariantMap = new Map();
shopifyProduct.variants.edges.forEach(edge => {
  if (edge.node && edge.node.sku) {
    shopifyVariantMap.set(edge.node.sku, edge.node.id);
  }
});

const primaryMockupUrlMap = new Map();
for (const imageUrl of allCuratedImageUrls) {
    try {
        const printifyVariantId = parseInt(imageUrl.split('/')[5], 10);
        if (!primaryMockupUrlMap.has(printifyVariantId)) {
            primaryMockupUrlMap.set(printifyVariantId, imageUrl);
        }
    } catch (e) {
        console.warn(`Could not parse Printify Variant ID from URL: ${imageUrl}`);
    }
}

const variantsUpdatePayload = [];

// Create a list of variant objects for the bulk update
for (const printifyVariant of printifyProduct.variants) {
    const printifySku = printifyVariant.sku;
    const shopifyVariantId = shopifyVariantMap.get(printifySku);
    const primaryImageUrl = primaryMockupUrlMap.get(printifyVariant.id);
    const shopifyImageId = shopifyMediaMap.get(primaryImageUrl);

    if (shopifyVariantId && shopifyImageId) {
        variantsUpdatePayload.push({
            id: shopifyVariantId,
            mediaId: shopifyImageId // <-- CORRECTED FIELD NAME (singular, no 's')
        });
    }
}

// --- FALLBACK FOR PRODUCTS WITH SHARED IMAGES (e.g., T-Shirts) ---
if (![937, 635, 764, 421].includes(blueprintId) && uploadedMediaEdges.length > 0) {
    const firstImageId = uploadedMediaEdges[0].node.id;
    if (firstImageId) {
      for (const shopifyVariantId of shopifyVariantMap.values()) {
          if (!variantsUpdatePayload.some(p => p.id === shopifyVariantId)) {
            variantsUpdatePayload.push({ 
                id: shopifyVariantId, 
                mediaId: firstImageId // <-- CORRECTED FIELD NAME (singular, no 's')
            });
          }
      }
    }
} else if ([937].includes(blueprintId) && uploadedMediaEdges.length > 0) {
    const sharedImageId = uploadedMediaEdges[0].node.id;
    for (const shopifyVariantId of shopifyVariantMap.values()) {
        variantsUpdatePayload.push({ 
            id: shopifyVariantId, 
            mediaId: sharedImageId // <-- CORRECTED FIELD NAME (singular, no 's')
        });
    }
}

// Return ONE single object formatted for the bulk update mutation
return {
    json: {
        productId: shopifyProduct.id,
        variants: variantsUpdatePayload
    }
};

