    sourceItem => sourceItem.json.collection_name === collection.title
  );

  if (!collection || !sourceDataItem) return; // skip if either is missing

  const sourceData = sourceDataItem.json;
  const publicId = sourceData.public_id;

  // Combine clothing and other products into one loop
  const allProducts = [...printifyProducts.clothing, ...printifyProducts.other];

  allProducts.forEach(product => {
    const imageUrl = buildImageUrl(product.product_type, publicId);

    outputItems.push({
      json: {
        session_id:            sourceData.session_id,
        email:                 sourceData.email,
        petname:               sourceData.petname,
        customer_name:         sourceData.customer_name,
        promptIndex:           sourceData.promptIndex,
        theme_name:            sourceData.theme_name,
        shopify_collection_id: collection.id,

        product_type:          product.product_type,
        image_url:             imageUrl,
        blueprint_id:          product.blueprint_id,
        print_provider_id:     product.print_provider_id,
        product_title:         `${sourceData.theme_name} - ${product.name}`,
        template_name:         product.name,
        required_variant_ids:  product.variant_id 
      }
    });

    console.log(`✅ Created ${product.product_type} product with URL: ${imageUrl}`);
  });
});

return outputItems;

