const productData = $input.item.json.data.product;

// Check if the product data or media edges exist
if (!productData || !productData.media || !productData.media.edges) {
  throw new Error("Could not find media data in the product response from Shopify.");
}

// Check if any media item is still being processed.
// We use .toLowerCase() to handle both "PROCESSING" and "processing".
const isStillProcessing = productData.media.edges.some(
  edge => edge.node.status.toLowerCase() === 'processing'
);

// isReady will be true only if NOTHING is still processing
const isReady = !isStillProcessing;

// Pass the readiness status and the full product data along
return {
  json: {
    isReady: isReady,
    processedProductData: productData
  }
};
