const outputItems = [];

cloudinaryResponses.forEach((cloudinaryItem, loopIndex) => {
  const cloudinaryResponseJson = cloudinaryItem.json; // This is the actual Cloudinary API response data

  let matchingOriginalDetails = null;

  // Strategy 1: Match using public_id (from Cloudinary) and customFilename (from original details)
  // Cloudinary public_id is often "folder/customFilename" or just "customFilename"
  const publicIdParts = cloudinaryResponseJson.public_id.split('/');
  const cloudinaryFilenamePart = publicIdParts[publicIdParts.length - 1]; // e.g., "session_id_promptX"

  const foundItem = allOriginalDetailsItems.find(
    origItem => origItem.json.customFilename === cloudinaryFilenamePart
  );
  if (foundItem) {
    matchingOriginalDetails = foundItem.json;
  }

  // Strategy 2: Fallback to index-based matching if filename match failed (less ideal but a backup)
  // This assumes UploadTOCloudery processes and outputs items in the same order it received them.
  if (!matchingOriginalDetails && cloudinaryResponses.length === allOriginalDetailsItems.length) {
    console.warn(`Combine Cloudinary: Could not match Cloudinary response (public_id: ${cloudinaryResponseJson.public_id}) to original details by filename. Falling back to index ${loopIndex}.`);
    if (allOriginalDetailsItems[loopIndex]) {
        matchingOriginalDetails = allOriginalDetailsItems[loopIndex].json;
    }
  }

  if (!matchingOriginalDetails) {
    console.error(`Combine Cloudinary: CRITICAL - Could not find matching original details for Cloudinary response with public_id: ${cloudinaryResponseJson.public_id}. Skipping this item.`);
    outputItems.push({ json: { ...cloudinaryResponseJson, error: "Orphaned Cloudinary response - no matching original details." } });
    return; // Skips to the next iteration of forEach
  }

  // Successfully matched, now combine them
  const combinedJson = {
    // All fields from Cloudinary's response
    ...cloudinaryResponseJson, // This includes asset_id, public_id, secure_url, existing, etc.

    // All fields from the matching originalDetails
    // This will overwrite any same-named fields from cloudinaryResponseJson if they exist in matchingOriginalDetails,
    // but mostly they should be distinct (e.g., theme_name, promptIndex are from originalDetails)
    ...matchingOriginalDetails
  };

  outputItems.push({ json: combinedJson });
});

