  console.warn(`‚ö†Ô∏è No mockup configuration found for blueprint_id: ${blueprintId}. No curated mockups will be generated.`);
  return [{ json: { ...createdProduct, curatedImageUrls: [] } }];
}

const newProductId = createdProduct.id;
const curatedImageUrls = [];

if (config.generatePerVariant) {
  // Logic for products like mugs and canvases
  console.log(`üöÄ Generating mockups for each variant for Blueprint ID: ${blueprintId}`);
