// Get data from the merged input (propagated + fresh media)
const printifyProduct = $input.item.json.printifyProduct;
const shopifyProduct = $input.item.json.shopifyProduct;
const uploadResult = $input.item.json.data?.product;  // Fresh from Fetch
const allCuratedImageUrls = $input.item.json.allCuratedImageUrls;
const blueprintId = $input.item.json.blueprintId;

// --- ERROR HANDLING ---
if (!printifyProduct || !shopifyProduct || !uploadResult || !allCuratedImageUrls) {
  throw new Error("Could not find required data. Check upstream nodes or Merge configuration.");
}

// Check for media processing issues
const uploadedMediaEdges = uploadResult.media.edges;
if (allCuratedImageUrls.length !== uploadedMediaEdges.length) {
    throw new Error(`Media count mismatch. Sent ${allCuratedImageUrls.length} images but received ${uploadedMediaEdges.length}.`);
}
uploadedMediaEdges.forEach((edge, index) => {
  if (!edge.node.image || !edge.node.image.id) {
    throw new Error(`Image ${index + 1} not processed (null). Increase Wait1 or check Shopify errors.`);
  }
});

// --- SKU-BASED MAPPING LOGIC (unchanged below) ---
// 1. Map the original Printify URL to the new Shopify Image ID (relies on order)
const shopifyMediaMap = new Map();
for (let i = 0; i < allCuratedImageUrls.length; i++) {
  shopifyMediaMap.set(allCuratedImageUrls[i], uploadedMediaEdges[i].node.image.id);  // Use image.id for variant imageId
}

// 2. Map the Shopify variant SKU to its GraphQL ID for a reliable lookup
const shopifyVariantMap = new Map();
shopifyProduct.variants.edges.forEach(edge => {
  if (edge.node && edge.node.sku) { // Use SKU as the key
    shopifyVariantMap.set(edge.node.sku, edge.node.id); // The value is the full GID
  }
});

// 3. Map the Printify variant ID to its primary mockup URL
const primaryMockupUrlMap = new Map();
for (const imageUrl of allCuratedImageUrls) {
    const printifyVariantId = parseInt(imageUrl.split('/')[5], 10);
    if (!primaryMockupUrlMap.has(printifyVariantId)) {
        primaryMockupUrlMap.set(printifyVariantId, imageUrl);
    }
}

const variantsUpdatePayload = [];

// 4. Loop through the original Printify variants to build the final payload
for (const printifyVariant of printifyProduct.variants) {
    const printifySku = printifyVariant.sku;
    const shopifyVariantId = shopifyVariantMap.get(printifySku); // This will be the GID
    const primaryImageUrl = primaryMockupUrlMap.get(printifyVariant.id);
    const shopifyImageId = shopifyMediaMap.get(primaryImageUrl);

    if (shopifyVariantId && shopifyImageId) {
        variantsUpdatePayload.push({
            id: shopifyVariantId, // Use the GID directly
            imageId: shopifyImageId  // Correct field for variant primary image
        });
    }
}

// Fallback for non-variant products (like T-Shirts)
if (![937, 635, 764].includes(blueprintId) && uploadedMediaEdges.length > 0) {
    const firstImageId = uploadedMediaEdges[0].node.image.id;  // Use first image.id
    if (firstImageId) {
      for (const shopifyVariantId of shopifyVariantMap.values()) {
          if (!variantsUpdatePayload.some(p => p.id === shopifyVariantId)) {
            variantsUpdatePayload.push({ id: shopifyVariantId, imageId: firstImageId });
          }
      }
    }
}

// Return the final payload for the 'productVariantsBulkUpdate' mutation
return {
    json: {
        productId: shopifyProduct.id,
        variants: variantsUpdatePayload
    }
};

