      gelato_template_id: originalData.gelato_template_id,
      original_data: originalData
    }
  }];
}

// FIXED: Create ALL variants in a single product request
const variantsArray = [];

variants.forEach((variant, variantIndex) => {
  console.log(`Processing variant ${variantIndex + 1}/${variants.length}: ${variant.title}`);

  // Get image placeholders for this variant
  const imagePlaceholders = variant.imagePlaceholders || [];

  // Find the main image placeholder
  // Prioritize 'front', then names including 'front' or 'image', fallback to the first placeholder
  const mainImagePlaceholder = imagePlaceholders.find(p =>
    p.printArea === "front"
  ) || imagePlaceholders.find(p =>
     p.name && (p.name.toLowerCase().includes("front") || p.name.toLowerCase().includes("image"))
  ) || imagePlaceholders[0];


  if (mainImagePlaceholder) {
    variantsArray.push({
      templateVariantId: variant.id,
      imagePlaceholders: [
        {
          name: mainImagePlaceholder.name,
          fileUrl: originalData.image_url
        }
      ]
    });
    console.log(`✓ Added variant ${variantIndex + 1}: ${variant.title} with placeholder: ${mainImagePlaceholder.name}`);
    console.log(`✓ Image URL: ${originalData.image_url}`);
  } else {
    console.log(`✗ No image placeholder found for variant: ${variant.title}`);
    console.log(`Available placeholders:`, imagePlaceholders.map(p => p.name));
    // Optionally, you might want to skip this variant or return an error
    // For now, we just log and skip adding this variant to the variantsArray
  }
});

console.log(`=== FINAL VARIANTS ARRAY ===`);
console.log(`Total variants to create: ${variantsArray.length}`);
console.log(`Variants array:`, JSON.stringify(variantsArray, null, 2));

// CORRECTED: Return a SINGLE item with ALL variants for the product,
// putting the data needed for the API request body directly at the top level.
return [{
