    templateId: originalData.gelato_template_id, // Directly accessible as item.json.templateId in the next node
    title: `${originalData.petname} ${originalData.theme_name} - ${originalData.template_name}`, // Directly accessible as item.json.title
  description: templateResponse.description || `<p>Custom ${originalData.theme_name} artwork featuring ${originalData.petname}. High-quality print-on-demand product perfect for pet lovers.</p>`,

    isVisibleInTheOnlineStore: true,
    salesChannels: ["web"],
    variants: variantsArray, // Directly accessible as item.json.variants
    tags: [originalData.petname, originalData.theme_name, "custom-pet-art"],
    productType: "Custom Pet Artwork",
    vendor: "Gauger Pet Art",

