// Function to extract data from collection
function extractDataFromCollection(collection) {
  console.log('=== EXTRACTING DATA FROM COLLECTION ===');
  console.log('Collection ID:', collection.id);
  console.log('Collection Title:', collection.title);
  console.log('Collection Handle:', collection.handle);
  console.log('Collection Image URL:', collection.image?.src);

  // Initialize variables
  let session_id = "";
  let promptIndex = 1;

  // FIXED: Extract from Shopify CDN URLs
  if (collection.image && collection.image.src) {
    console.log('Attempting to extract from image URL:', collection.image.src);

    // Updated regex for Shopify CDN URLs - try multiple patterns
    let urlMatch = collection.image.src.match(/collections\/([a-f0-9]+)_prompt(\d+)\.(png|jpg|jpeg|webp)/);
    if (!urlMatch) {
      // Try without collections/ prefix
      urlMatch = collection.image.src.match(/([a-f0-9]+)_prompt(\d+)\.(png|jpg|jpeg|webp)/);
    }
    if (!urlMatch) {
      // Try without file extension
      urlMatch = collection.image.src.match(/([a-f0-9]+)_prompt(\d+)/);
    }

    if (urlMatch) {
      session_id = urlMatch[1];
      promptIndex = parseInt(urlMatch[2]);
      console.log('✓ Successfully extracted from URL - Session ID:', session_id, 'Prompt Index:', promptIndex);
    } else {
      console.log('❌ Failed to extract from image URL');
    }
  }

  // Fallback 1: Extract session_id from collection handle
  if (!session_id) {
    const handleMatch = collection.handle.match(/([a-f0-9]+)$/);
    if (handleMatch) {
      session_id = handleMatch[1];
      console.log('✓ Extracted session_id from handle:', session_id);
    }
  }

  // Fallback 2: Map theme names to prompt indices
  const titleParts = collection.title.split(' ');
  const petname = titleParts[0];
  const theme_name = titleParts.slice(1).join(' ');

  // If we still don't have promptIndex, map from theme
  if (promptIndex === 1 && theme_name !== "Spa Day") {
    const themeToPromptIndex = {
      "Spa Day": 1,
      "Midnight Snack": 2,
      "Barbecue Chef": 3
    };

    promptIndex = themeToPromptIndex[theme_name] || 1;
    console.log('✓ Mapped theme to prompt index:', theme_name, '->', promptIndex);
  }

  // Extract customer info from body_html
  let customer_name = "";
  let email = "";
  if (collection.body_html) {
    const bodyMatch = collection.body_html.match(/Created for (.+?) \((.+?)\)/);
    if (bodyMatch) {
      customer_name = bodyMatch[1];
      email = bodyMatch[2];
    }
  }

  // Generate the correct URLs using the extracted data
  const cloudName = "myfurryprints";
  const maskId = "Mask3larger_jpm63l";
  const publicId = `${session_id}/${session_id}_prompt${promptIndex}`;

  const clothing_url = `https://res.cloudinary.com/${cloudName}/image/upload/e_upscale/f_webp/c_scale,w_4000/l_${maskId},w_4000,fl_cutter/${publicId}`;
  const other_url = `https://res.cloudinary.com/${cloudName}/image/upload/e_upscale/f_webp/c_scale,w_4000/${publicId}`;

  console.log('Final Extracted Data:');
  console.log('  Session ID:', session_id);
  console.log('  Prompt Index:', promptIndex);
  console.log('  Pet Name:', petname);
  console.log('  Theme Name:', theme_name);
  console.log('  Customer Name:', customer_name);
  console.log('  Email:', email);
  console.log('  Clothing URL:', clothing_url);
  console.log('  Other URL:', other_url);

  return {
    session_id: session_id,
    email: email,
    petname: petname,
    customer_name: customer_name,
    promptIndex: promptIndex,
    theme_name: theme_name,
    clothing_url: clothing_url,
    other_url: other_url
  };
}

// Process each collection response
items.forEach((item, itemIndex) => {
  const collection = item.json.custom_collection;

  if (collection && collection.id) {
    console.log(`\n=== PROCESSING COLLECTION ${itemIndex + 1} ===`);

    // Extract data from collection
    const originalData = extractDataFromCollection(collection);

    // Create clothing products
    gelatoTemplates.clothing.forEach((template, templateIndex) => {
      const productData = {
        json: {
          // Collection data from Shopify response
          shopify_collection_id: collection.id,
          collection_handle: collection.handle,
          collection_title: collection.title,

          // Extracted customer data
          session_id: originalData.session_id,
          email: originalData.email,
          petname: originalData.petname,
          customer_name: originalData.customer_name,
          promptIndex: originalData.promptIndex,
          theme_name: originalData.theme_name,

          // Product data with THEME-SPECIFIC image
