          image_url: originalData.clothing_url,
          gelato_template_id: template.id,
          template_name: template.name,
          product_title: `${originalData.petname} ${originalData.theme_name} - ${template.name}`,
          product_sku: `${originalData.session_id}-${originalData.promptIndex}-${template.id.slice(-8)}`
        }
      };

      outputItems.push(productData);

      console.log(`✓ Created clothing product: ${originalData.petname} ${originalData.theme_name} - ${template.name}`);
      console.log(`  Prompt Index: ${originalData.promptIndex}`);
      console.log(`  Image URL: ${originalData.clothing_url}`);
    });

    // Create other products
    gelatoTemplates.other.forEach((template, templateIndex) => {
      const productData = {
        json: {
          // Collection data from Shopify response
          shopify_collection_id: collection.id,
          collection_handle: collection.handle,
          collection_title: collection.title,

          // Extracted customer data
          session_id: originalData.session_id,
          email: originalData.email,
          petname: originalData.petname,
          customer_name: originalData.customer_name,
          promptIndex: originalData.promptIndex,
          theme_name: originalData.theme_name,

          // Product data with THEME-SPECIFIC image
          product_type: "other",
          image_url: originalData.other_url,
          gelato_template_id: template.id,
          template_name: template.name,
          product_title: `${originalData.petname} ${originalData.theme_name} - ${template.name}`,
          product_sku: `${originalData.session_id}-${originalData.promptIndex}-${template.id.slice(-8)}`
        }
      };

      outputItems.push(productData);

      console.log(`✓ Created other product: ${originalData.petname} ${originalData.theme_name} - ${template.name}`);
      console.log(`  Prompt Index: ${originalData.promptIndex}`);
      console.log(`  Image URL: ${originalData.other_url}`);
    });
