});

console.log('\n=== FINAL OUTPUT SUMMARY ===');
console.log(`Total products created: ${outputItems.length}`);

return outputItems;

// Node: Collect All Products
const allItems = $input.all();
const shopifyCollectionsMap = new Map(); // Key: "PetName ThemeName", Value: { collection_id, handle, title }
const productsToProcess = [];

console.log(`Collect All Products - Received ${allItems.length} items from Merge1.`);

// --- First Pass: Populate shopifyCollectionsMap from Shopify Collection Items ---
allItems.forEach((item, index) => {
  // Shopify Collection items have collection_id and their title is "PetName ThemeName"
  if (item.json.collection_id && item.json.title && typeof item.json.title === 'string' && !item.json.id) { // !item.json.id helps distinguish from Gelato product
    const shopifyCollectionTitleKey = item.json.title; // e.g., "moon Spa Day"
    if (!shopifyCollectionsMap.has(shopifyCollectionTitleKey)) {
      shopifyCollectionsMap.set(shopifyCollectionTitleKey, {
        collection_id: item.json.collection_id,
        collection_handle: item.json.handle,
        original_shopify_collection_title: item.json.title
      });
      console.log(`Mapped Shopify Collection: "${shopifyCollectionTitleKey}" -> ID: ${item.json.collection_id}`);
    }
  }
});

if (shopifyCollectionsMap.size === 0) {
  console.warn("No Shopify collections were identified and mapped from Merge1. Downstream processing will likely fail.");
} else {
  console.log(`Total Shopify collections mapped: ${shopifyCollectionsMap.size}`);
  console.log("Shopify Collections Map Keys:", Array.from(shopifyCollectionsMap.keys()));
}

// --- Second Pass: Match Gelato products to Shopify collections ---
allItems.forEach((item, index) => {
  // Gelato product items have an 'id' (Gelato product ID) and 'tags'
  if (item.json.id && item.json.tags && Array.isArray(item.json.tags) && item.json.tags.length >= 2 && item.json.title && item.json.title.includes(' - ')) {
    const gelatoProductTitle = item.json.title; // Full title like "moon Spa Day - Custom Apparel"
    const petname = item.json.tags[0];       // e.g., "moon"
    const themeName = item.json.tags[1];     // e.g., "Spa Day"

    const shopifyCollectionTitleKey = `${petname} ${themeName}`; // Construct the key, e.g., "moon Spa Day"

    const collectionData = shopifyCollectionsMap.get(shopifyCollectionTitleKey);

    if (collectionData) {
      productsToProcess.push({
        json: {
          // This is the full Gelato product title, which should also be the Shopify product title after sync
          product_title_to_search_in_shopify: gelatoProductTitle, 

          // Shopify collection details for adding the product later
          shopify_collection_id: collectionData.collection_id,
          shopify_collection_handle: collectionData.collection_handle,
          shopify_collection_original_title: collectionData.original_shopify_collection_title,

          // For debugging or context
          gelato_product_id: item.json.id,
          derived_petname: petname,
          derived_themename: themeName,
          // gelato_product_details: item.json // Uncomment if you need the whole Gelato item
        }
      });
      console.log(`Product "${gelatoProductTitle}" (Gelato ID: ${item.json.id}) will be searched in Shopify and added to collection "${shopifyCollectionTitleKey}" (Shopify Collection ID: ${collectionData.collection_id})`);
    } else {
      console.warn(`No Shopify collection found for Gelato product: "${gelatoProductTitle}" (using key: "${shopifyCollectionTitleKey}" from tags). This product will be skipped.`);
    }
  }
});

if (productsToProcess.length === 0) {
  console.warn("No Gelato products were successfully matched to Shopify collections. Check product titles/tags and collection titles alignment.");
}

console.log(`Total products to process (search in Shopify and add to collection): ${productsToProcess.length}`);
return productsToProcess;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();

const currentProductData = $input.item.json;
return {
  json: {
    product_title: currentProductData.product_title_to_search_in_shopify, // Renamed for next node
    shopify_collection_id: currentProductData.shopify_collection_id,       // Passed through
    original_search_title: currentProductData.product_title_to_search_in_shopify, // Context
    original_shopify_collection_title: currentProductData.shopify_collection_original_title, // Context
    gelato_product_id_context: currentProductData.gelato_product_id // Context
  }
};

// Get the direct API response from the SearchShopifyProduct node
const shopifyApiResponse = $input.item.json; // This is the output of SearchShopifyProduct

// Get the data that was *sent into* SearchShopifyProduct (which was the output of 'Code' node)
// $('Node Name').item.json refers to the output of 'Node Name' FOR THE CURRENT LOOP ITERATION.
// Node 'Code' is the one before SearchShopifyProduct at [1560, 980]
const dataFromPreviousCodeNode = $('Format Product Search Data').item.json;

let shopifyProductId = null;
const shopifyApiProducts = shopifyApiResponse.products;
const searchedTitleInShopify = dataFromPreviousCodeNode.product_title; // Title used for the search

if (shopifyApiProducts && shopifyApiProducts.length > 0) {
  // Prefer exact match if multiple products are returned by search
  const exactMatch = shopifyApiProducts.find(p => p.title === searchedTitleInShopify);
  if (exactMatch) {
    shopifyProductId = exactMatch.id;
    console.log(`Found EXACT MATCH Shopify product: "${exactMatch.title}" (Shopify ID: ${shopifyProductId}) for searched title "${searchedTitleInShopify}"`);
  } else {
    // Fallback to the first product if no exact match
    shopifyProductId = shopifyApiProducts[0].id;
    console.warn(`No exact title match in Shopify for "${searchedTitleInShopify}". Using first found: "${shopifyApiProducts[0].title}" (Shopify ID: ${shopifyProductId}).`);
  }
} else {
  console.error(`Shopify Product NOT FOUND in Shopify for title: "${searchedTitleInShopify}". (Context Gelato ID: ${dataFromPreviousCodeNode.gelato_product_id_context}).`);
  // If product not found, set product_id to null so Add_Product_To_Collection will fail gracefully (if onError is set to continue)
  shopifyProductId = null;
}

return {
  json: {
    product_id: shopifyProductId,
    collection_id: dataFromPreviousCodeNode.shopify_collection_id, // <-- Pulled from Code node's output
    collection_handle: dataFromPreviousCodeNode.shopify_collection_handle, // <-- ADDED: Pass the handle
    gelato_product_id_context: dataFromPreviousCodeNode.gelato_product_id_context, // Pass context ID

    // Keep original context data for logging/debugging in the next node (Code4)
    context_searched_title: searchedTitleInShopify,
    context_original_shopify_collection_title: dataFromPreviousCodeNode.original_shopify_collection_title,
    context_gelato_product_id: dataFromPreviousCodeNode.gelato_product_id_context, // Duplicate, but harmless
    context_collection_handle: dataFromPreviousCodeNode.shopify_collection_handle // ADDED: Pass handle in context too
  }
};

const shopifyCollectApiResponse = $input.first().json;
const contextData = $('Extract Shopify Product ID').item.json; // Data from Code3

if (shopifyCollectApiResponse.collect && shopifyCollectApiResponse.collect.id) {
  console.log(`✅ SUCCESS: Shopify Product (Shopify ID: ${shopifyCollectApiResponse.collect.product_id}, originally searched as "${contextData.context_searched_title}") added to Shopify Collection (Shopify ID: ${shopifyCollectApiResponse.collect.collection_id}, title: "${contextData.context_original_shopify_collection_title}"). Gelato Product ID was: ${contextData.context_gelato_product_id}.`);
} else {
